fn construct_suffix_array(text: &str) -> Vec<usize> {
    let mut suffix_array = Vec::new();
    for i in 0..text.len() {
        suffix_array.push((text[i..].to_string(), i));
    }
    suffix_array.sort_by_key(|(suffix, _)| suffix.clone());
    suffix_array.iter().map(|(_, i)| *i).collect()
}

fn main() {
    let text = "$";
    let suffix_array = construct_suffix_array(text);
    println!("{:?}", suffix_array);
}